    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        double basico, trabajo;
        basico = Integer.parseInt(txtBasico.getText());
        trabajo = Integer.parseInt(txtTrabajo.getText());
        if(Ejercicio422.salario(trabajo, basico)>450000){
            txtSalario.setText(String.valueOf(Ejercicio422.salario(trabajo, basico)));
        }
        else{
        txtSalario.setText("No Disponible");
        }
    }//GEN-LAST:event_btnMostrarActionPerformed

